package com.twjitm.transaction.config;/** * @author twjitm - [Created on 2018-10-17 15:16] */import redis.clients.jedis.JedisCluster;import redis.clients.jedis.JedisPool;/** * 有关的相关配置 */public class TransactionConfig {    /**     * redis相关的配置     */    private JedisPool jedisPool;    /**     * 集群模式     */    private JedisCluster jedisCluster;    /**     * zookeeper模式     */    private ZookeeperConfigNode zookeeperConfigNode;    /**     * 数据源类型     */    private TransactionDataSourceType dataSourceType;    public ZookeeperConfigNode getZookeeperConfigNode() {        return zookeeperConfigNode;    }    public void setZookeeperConfigNode(ZookeeperConfigNode zookeeperConfigNode) {        this.zookeeperConfigNode = zookeeperConfigNode;    }    public JedisPool getJedisPool() {        return jedisPool;    }    public void setJedisPool(JedisPool jedisPool) {        this.jedisPool = jedisPool;    }    public JedisCluster getJedisCluster() {        return jedisCluster;    }    public void setJedisCluster(JedisCluster jedisCluster) {        this.jedisCluster = jedisCluster;    }}