package com.twjitm.transaction.service.transaction;import com.twjitm.transaction.transaction.entity.AbstractNettyTransactionEntity;import com.twjitm.transaction.transaction.entity.AbstractNettyTransactionZkEntity;import com.twjitm.transaction.transaction.enums.NettyTransactionCause;import com.twjitm.transaction.transaction.enums.NettyTransactionCommitResult;/** * 事物对外提供的服务 * 可以批量提交事物 * * @author twjitm - [Created on 2018-08-27 15:42] * @company https://github.com/twjitm * @jdk java version "1.8.0_77" */public interface NettyTransactionService {    /**     * redis 模式提交事务     * @param cause     * @param abstractGameTransactionEntity     * @return     */    NettyTransactionCommitResult commitTransaction(NettyTransactionCause cause, AbstractNettyTransactionEntity... abstractGameTransactionEntity);    /**     * redis 模式提交事务     * @param cause     * @param waitTime     * @param abstractGameTransactionEntity     * @return     */    NettyTransactionCommitResult commitTransaction(NettyTransactionCause cause, long waitTime, AbstractNettyTransactionEntity... abstractGameTransactionEntity);    /**     * zookeeper 模式来提交事物锁     *     * @param cause     * @param abstractNettyTransactionZkEntities     * @return     */    NettyTransactionCommitResult commitTransaction(NettyTransactionCause cause, AbstractNettyTransactionZkEntity... abstractNettyTransactionZkEntities);}