package com.twjitm.transaction.transaction.enums;import com.twjitm.transaction.lock.NettyTransactionLock;/** * <p> * 游戏事物实体产生的原因 * 实体中的原因，将用于实体锁的键，控制着唯一实体键值的关键 * 具体应用查看 * {@link NettyTransactionLock#getLockKey(String, NettyTransactionEntityCause)}方法 * *</p> * @author twjitm - [Created on 2018-08-27 15:29] * @company https://github.com/twjitm * @jdk java version "1.8.0_77" */public class NettyTransactionEntityCause {    private String cause;    public NettyTransactionEntityCause(String cause) {        this.cause = cause;    }    public String getCause() {        return cause;    }    public void setCause(String cause) {        this.cause = cause;    }}