package com.twjitm.transaction.transaction.exception;import com.twjitm.transaction.transaction.enums.NettyTransactionCommitResult;/** * 事物执行异常类 * * @author twjitm - [Created on 2018-08-27 10:08] * @company https://github.com/twjitm * @jdk java version "1.8.0_77" */public class NettyTransactionException extends RuntimeException {    /**     * 异常信息     */    private String message;    /**     * 事务返回结果     */    private NettyTransactionCommitResult result;    public NettyTransactionException(String message) {        super(message);        this.message = message;    }    public NettyTransactionException() {        super();    }    public NettyTransactionException(NettyTransactionCommitResult result) {        super();        this.result = result;    }    @Override    public String getMessage() {        return message;    }    public NettyTransactionCommitResult getResult() {        return result;    }}